  ```
  define([], function() {
    'use strict';
    class QueryString {
      constructor(){
        let search = window.location.search.substring(1).split('&');
        let tempGroup = [];

        for (let p of search) {
          tempGroup.push(p.split('='));
        }
        this.params = new Map(tempGroup);
      }

      getValue(name){
        return this.params.get(name);
      }
    }

    let queryString = new QueryString();
